#![allow(unused_imports)]

use crate::blockdata::transaction::{Transaction, ExtraField};
use crate::consensus::encode::{serialize, deserialize, deserialize_partial};
use crate::cryptonote::hash::Hashable;

#[test]
fn deserialize_transaction() {
    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("84d48dc11ec91950f8b70a85af9db91fe0c8abef71ef5db08304f7344b99ea66", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("3bc7ff015b227e7313cc2e8668bfbb3f3acbee274a9c201d6211cf681b5f6bb1", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("9e3f73e66d7c7293af59c59c1ff5d6aae047289f49e5884c66caaf4aea49fb34", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("f17d8275b1f4f0975238d7336886fc24dddee5bc0589bd36e9e38d1871e8c44c", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("b047cd940bb67687ab30de1466b5145695fe6a5da465a60ca41d6caa4239c3f2", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("afe49b674dac27b6c42a5bb48122d79650fb919b2724cf8ff535de364418c234", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("f06eaf024b6f496f39c17c5ec803c56198c6ebbd7242452b036f488a1b0e28dd", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("924f4130be653cd4d55bd6282c76e13e97bf4cc92a68d6bf4a53a62955e038d4", tx.hash().to_string());

    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert_eq!(true, tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx));
    assert_eq!("30fd251955bf7c42c3f7341e48454038b82e96018ac7a4fc7c546c3b8fec2fa6", tx.hash().to_string());
}
