use monero::blockdata::transaction::{Transaction, TransactionPrefix, TxIn, TxOut, TxOutTarget};
use monero::consensus::encode::{deserialize, serialize, VarInt};
use monero::cryptonote::hash::Hashable;
use monero::util::key::PublicKey;
use std::str::FromStr;

#[test]
fn deserialize_transaction_1() {
    let hex = hex::decode("").unwrap();
    let tx = deserialize::<Transaction>(&hex[..]);
    assert!(tx.is_ok());
    let tx = tx.unwrap();
    assert_eq!(hex, serialize(&tx).unwrap());
    assert_eq!(
        "84d48dc11ec91950f8b70a85af9db91fe0c8abef71ef5db08304f7344b99ea66",
        format!("{:02x}", tx.hash().unwrap())
    );
}

